import { useState } from "react";
import MovieList from "./MovieList";
import Filter from "./Filter";
import AddMovie from "./AddMovie";

const Home = () => {
  const [movies, setMovies] = useState([
    {
      title: "Deputy",
      description: "The Deputy is a crime thriller about a young sheriff's deputy",
      posterURL: "data:image/jpeg;base64,",
      rating: 4.8,
      trailer: "https://www.youtube.com/embed/k2aQuMA-77g?si=_g76XIb3nB3ia8f9"
    },
    {
      title: "Love in Every Word",
      description: "The film explores themes of past experiences affecting current relationships, the clash between different personalities, and the challenges faced by financially independent women in finding love. .",
      posterURL: "data:image/jpeg;base64,",
      rating: 4.9,
      trailer: "https://www.youtube.com/embed/bslcx4LRFL0?si=Hblm0vP61SiFHwqT"
    }
  ]);

  const [filterTitle, setFilterTitle] = useState("");
  const [filterRating, setFilterRating] = useState("");

  const handleAddMovie = (movie) => {
    setMovies([...movies, movie]);
  };

  const filteredMovies = movies.filter(
    (movie) =>
      movie.title.toLowerCase().includes(filterTitle.toLowerCase()) &&
      (!filterRating || movie.rating >= parseFloat(filterRating))
  );

  return (
    <div>
      <AddMovie onAdd={handleAddMovie} />
      <Filter
        filterTitle={filterTitle}
        setFilterTitle={setFilterTitle}
        filterRating={filterRating}
        setFilterRating={setFilterRating}
      />
      <MovieList movies={filteredMovies} />
    </div>
  );
};

export default Home;
